package testing;

public class Or_opertor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=5;
		System.out.println(a<b||b<c);
		System.out.println(a<b| b<a);
		System.out.println(b<c||a<b);
		System.out.println(a++<b||b++<c);
		System.out.println(a |c);
		System.out.println(a>b||b<c);
		

	}

}
